var n = msg.payload.length;

var events = [];
var deb0 = 0;
var deb = 0;
var fin0 = 0;
var fin = 0;

//liste des débuts et fin d'événements en secondes
for (let i = 0; i < n; i++) {
    deb0 = msg.payload[i]["debut"].split(":");
    deb = parseInt(deb0[0])*3600 + parseInt(deb0[1])*60;
    fin0 = msg.payload[i]["fin"].split(":");
    fin = parseInt(fin0[0]) * 3600 + parseInt(fin0[1]) * 60;
    events.push([deb, fin]);
};

//calcul de l'instant présent en secondes
var d = new Date().toString().split(" ")[4];
var time = d.split(":");
var instant = parseInt(time[0])*3600 
    + parseInt(time[1])*60 
    + parseInt(time[2]);

//on regarde si la salle est réservée et on affiche en fonction
for (let i = 0; i<n; i++) {
    if (instant < events[i][1]) {
        if (instant > events[i][0]) {
            msg.payload = "réservée";
            return msg;
        }
        else {
            if (global.payload == "libre"){
                msg.payload = "libre";
                return msg;
            }

        }
    }
};

//si on a dépassé le dernier créneau réservé
